import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { CalendarSchedulerComponent } from './components/calendar-scheduler/calendar-scheduler.component';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, CalendarSchedulerComponent],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'Calendar Event Scheduler';
}
<div class="app-container">
  <header class="app-header">
    <h1>{{ title }}</h1>
    <p>Interactive day-view calendar with event scheduling and overlap detection</p>
  </header>

  <main class="app-main">
    <app-calendar-scheduler></app-calendar-scheduler>
  </main>

  <footer class="app-footer">
    <p>Click on any time slot to schedule a new event. Drag to adjust duration.</p>
  </footer>
</div>
